def create_pagination_keyboard1(current_page, total_pages):
    keyboard = types.InlineKeyboardMarkup(row_width=2)

    # Check if the current page is the first page
    if current_page == 1:
        # Only add the next button
        keyboard.insert(types.InlineKeyboardButton('‚ùØ', callback_data='next'))
    # Check if the current page is the last page
    elif current_page == total_pages:
        # Only add the previous button
        keyboard.insert(types.InlineKeyboardButton('‚ùÆ', callback_data='prev'))
    else:
        # Add both previous and next buttons
        keyboard.insert(types.InlineKeyboardButton('‚ùÆ', callback_data='prev'))
        keyboard.insert(types.InlineKeyboardButton('‚ùØ', callback_data='next'))

    # Add the module navigation buttons
    keyboard.add(types.InlineKeyboardButton(text="–ú–æ–¥—É–ª—å 2: –¢–µ—Ö–Ω–∏–∫–∏ –∏ –ª–∞–π—Ñ—Ö–∞–∫–∏", callback_data="module2"))
